mapper.select:
SELECT bk.id,bk.book_name,bk.book_des,bk.img,bk.type,bk.kind,bk.status,bk.author_name,bk.author_des,bk.ebook_url,bk.ebook_pwd,bk.borrow_num,bk.creator,bk.updator,bk.created,bk.updated,bk.yn,bt.name FROM book bk LEFT JOIN book_type bt on bk.type = bt.type where 1=1 AND bk.status != 40 AND bk.yn = 1
and bk.status = #{status}
and bk.book_name like '%${bookName}%'
and bk.type like '%${type}%'
AND bk.kind = #{kind}
ORDER BY bk.id DESC

mapper.update:
update book

  
    status = #{status},
  
  updated = now(),
  updator = #{updator}

  where id = #{id}

mapper.select:
SELECT bd.book_id, bd.user_id, bd.content, bd.created, u.nick_name, u.user_name FROM book_discuss bd LEFT JOIN users u on u.id = bd.user_id where 1=1
and bd.user_id = #{userId}
and bd.book_id = #{bookId}
ORDER BY bd.id DESC
